import React, { useState } from 'react';
import MUSIC_LOGO from "../images/MusicTest.png";
import RECIPE from "../images/Recipe.png";
import GALLERY from "../images/Gallery.png";

function BlogPage() {
    const [selectedOption, setSelectedOption] = useState('music');

    const renderContent = () => {
        switch(selectedOption) {
            case 'music':
                return <h2>Music Content</h2>;
            case 'cookbook':
                return <h2>Cookbook Content</h2>;
            case 'photo':
                return <h2>Photo Gallery Content</h2>;
            default:
                return <h2>Select an option</h2>;
        }
    };

    return (
        <div className="blog-container" style={{ display: 'flex', width: '100%', flexDirection: "row", alignItems: "center", justifyContent: "center", }}>
            <div className="option-container" style={{ width: '5%', height: '20vh', background: '#B2BEB5', margin: "0 0 0 100px" }}>
                <ul style={{ listStyleType: 'none', padding: '0', }}>
                    <li onClick={() => setSelectedOption('music')}>
                        <img src={MUSIC_LOGO} alt ="music" style={{ display: 'block', margin: "auto", width: '40px', height: 'auto', paddingBottom: "17px" }}/>
                    </li>
                    <li onClick={() => setSelectedOption('cookbook')}>
                        <img src={RECIPE} alt="recipe" style={{ display: 'block', margin: "auto", width: '40px', height: 'auto', paddingBottom: "17px" }}/>
                    </li>
                    <li onClick={() => setSelectedOption('photo')}>
                        <img src={GALLERY} alt="gallery" style={{ display: 'block', margin: "auto", width: '40px', height: 'auto' }}/>
                    </li>
                </ul>
            </div>
            <div className="option-content" style={{ flex: 1, overflowY: 'auto', height: '80vh', background: '#f4f4f4', margin: "20px 100px 0 100px" }}>
                {renderContent()}
            </div>
        </div>
    );
}

export default BlogPage;
