import React, { useState } from 'react';

function BlogPage() {
    const [selectedOption, setSelectedOption] = useState('music'); // Default selection

    const renderContent = () => {
        switch(selectedOption) {
            case 'music':
                return <h2>Music Content</h2>;
            case 'cookbook':
                return <h2>Cookbook Content</h2>;
            case 'photo gallery':
                return <h2>Photo Gallery Content</h2>;
            default:
                return <h2>Music Content</h2>;
        }
    };

    return (
        <div style={{ display: 'flex' }}>
            <div style={{ width: '200px', height: '100vh', background: '#f0f0f0' }}>
                <ul>
                    <li onClick={() => setSelectedOption('music')}>Music</li>
                    <li onClick={() => setSelectedOption('cookbook')}>Cookbook</li>
                    <li onClick={() => setSelectedOption('photo gallery')}>Photo Gallery</li>
                </ul>
            </div>
            <div style={{ flex: 1, padding: '20px' }}>
                {renderContent()}
            </div>
        </div>
    );
}

export default BlogPage;
