import React, { useState } from 'react';

function BlogPage() {
    const [selectedOption, setSelectedOption] = useState('music');

    const renderContent = () => {
        switch(selectedOption) {
            case 'music':
                return <h2>Music Content</h2>;
            case 'cookbook':
                return <h2>Cookbook Content</h2>;
            case 'photo':
                return <h2>Photo Gallery Content</h2>;
            default:
                return <h2>Music Content</h2>;
        }
    };

    return (
        <div className="blog-container">
            <div className="option-container">
                <ul>
                    <li onClick={() => setSelectedOption('music')}>
                        <h1>Music</h1>
                    </li>
                    <li onClick={() => setSelectedOption('cookbook')}>
                        <h1>Cook Book</h1>
                    </li>
                    <li onClick={() => setSelectedOption('photo')}>
                        <h1>Photo</h1>
                    </li>
                </ul>
            </div>
            <div className="option-content">
                {renderContent()}
            </div>
        </div>
    );
}

export default BlogPage;
