import React, { useState } from 'react';

function BlogPage() {
    const [selectedOption, setSelectedOption] = useState('music');

    const renderContent = () => {
        switch(selectedOption) {
            case 'music':
                return <h2>Music Content</h2>;
            case 'cookbook':
                return <h2>Cookbook Content</h2>;
            case 'photo':
                return <h2>Photo Gallery Content</h2>;
            default:
                return <h2>Select an option</h2>;
        }
    };

    return (
        <div className="blog-container" style={{ display: 'flex', width: '100%', flexDirection: "row", alignItems: "center", justifyContent: "center" }}>
            <div className="option-container" style={{ width: '10%', height: '20vh', background: '#f4f4f4', marginLeft: "100px", marginRight: "30px" }}>
                <ul style={{ listStyleType: 'none', padding: '0' }}>
                    <li onClick={() => setSelectedOption('music')}>
                        <h1>Music</h1>
                    </li>
                    <li onClick={() => setSelectedOption('cookbook')}>
                        <h1>Cook Book</h1>
                    </li>
                    <li onClick={() => setSelectedOption('photo')}>
                        <h1>Photo</h1>
                    </li>
                </ul>
            </div>
            <div className="option-content" style={{ flex: 1, overflowY: 'auto', height: '70vh', background: 'red', marginRight: "100px", marginLeft: "200px" }}>
                {renderContent()}
            </div>
        </div>
    );
}

export default BlogPage;
