import React, { useState } from 'react';

function BlogPage() {
    const [selectedOption, setSelectedOption] = useState('music');

    const renderContent = () => {
        switch(selectedOption) {
            case 'music':
                return <h2>Music Content</h2>;
            case 'cookbook':
                return <h2>Cookbook Content</h2>;
            case 'photo gallery':
                return <h2>Photo Gallery Content</h2>;
            default:
                return <h2>Music Content</h2>;
        }
    };

    return (
        <div style={{
            display: 'flex', 
            justifyContent: 'center', 
            height: '100vh', 
            alignItems: 'center'
        }}>
            <div style={{
                textAlign: 'center'
            }}>
                <img src="/path/to/logo.svg" alt="Logo" style={{ width: '100px', marginBottom: '20px' }}/>
                <div style={{
                    width: '200px'
                }}>
                    <ul style={{ listStyleType: 'none', padding: '0' }}>
                        <li style={{ margin: '10px 0', cursor: 'pointer' }} onClick={() => setSelectedOption('music')}>
                            <img src="/path/to/music-icon.svg" alt="Music" style={{ display: 'block', margin: 'auto', width: '40px', height: 'auto' }}/>
                        </li>
                        <li style={{ margin: '10px 0', cursor: 'pointer' }} onClick={() => setSelectedOption('cookbook')}>
                            <img src="/path/to/cookbook-icon.svg" alt="Cookbook" style={{ display: 'block', margin: 'auto', width: '40px', height: 'auto' }}/>
                        </li>
                        <li style={{ margin: '10px 0', cursor: 'pointer' }} onClick={() => setSelectedOption('photo gallery')}>
                            <img src="/path/to/gallery-icon.svg" alt="Photo Gallery" style={{ display: 'block', margin: 'auto', width: '40px', height: 'auto' }}/>
                        </li>
                    </ul>
                </div>
                <div style={{
                    marginTop: '20px'
                }}>
                    {renderContent()}
                </div>
            </div>
        </div>
    );
}

export default BlogPage;
